import React, { useState } from "react";
import "./step8.scss";

export default function Step8({ nextStep, prevStep }) {
  const [externalPhotos, setExternalPhotos] = useState([]);
  const [floorPlans, setFloorPlans] = useState([]);
  const [certificates, setCertificates] = useState([]);

  // Funkcja obsługująca zmianę plików
  const handleFileChange = (e, setter) => {
    const newFiles = Array.from(e.target.files);
    setter((prevFiles) => [...prevFiles, ...newFiles]);
  };

  // Funkcja do usuwania pliku
  const removeFile = (fileToRemove, setter) => {
    setter((prevFiles) => prevFiles.filter((file) => file !== fileToRemove));
  };

  return (
    <div className="step8">
      <h2>Załaduj zdjęcia</h2>

      <div className="fileUploaders">
        {/* Pierwszy przycisk: Zdjęcia budynku z zewnątrz */}
        <div className="fileUploader">
          <label className="custom-file-upload">
            <input
              type="file"
              multiple
              onChange={(e) => handleFileChange(e, setExternalPhotos)}
            />
            {/* Zdjęcia budynku z zewnątrz */}
          </label>

          <div className="file-preview">
            {externalPhotos.map((file, index) => (
              <div key={index} className="file-item">
                <p>{file.name}</p>
                <button onClick={() => removeFile(file, setExternalPhotos)}>Usuń</button>
              </div>
            ))}
          </div>
        </div>

        {/* Drugi przycisk: Rzuty nieruchomości */}
        <div className="fileUploader">
          <label className="custom-file-upload">
            <input
              type="file"
              multiple
              onChange={(e) => handleFileChange(e, setFloorPlans)}
            />
            Rzuty nieruchomości
          </label>

          <div className="file-preview">
            {floorPlans.map((file, index) => (
              <div key={index} className="file-item">
                <p>{file.name}</p>
                <button onClick={() => removeFile(file, setFloorPlans)}>Usuń</button>
              </div>
            ))}
          </div>
        </div>

        {/* Trzeci przycisk: Świadectwo budynku */}
        <div className="fileUploader">
          <label className="custom-file-upload">
            <input
              type="file"
              multiple
              onChange={(e) => handleFileChange(e, setCertificates)}
            />
            Świadectwo budynku
          </label>

          <div className="file-preview">
            {certificates.map((file, index) => (
              <div key={index} className="file-item">
                <p>{file.name}</p>
                <button onClick={() => removeFile(file, setCertificates)}>Usuń</button>
              </div>
            ))}
          </div>
        </div>
      </div>

      <button onClick={prevStep}>Wstecz</button>
      <button onClick={nextStep}>Zakończ</button>
    </div>
  );
}
