import React, { useEffect, useState } from "react";
import "./formComponent.scss";

export default function FormComponent({updateProgress}) {
  const totalFields = 5;
  const [formData, setFormData] = useState({
    field1: " ",
    field2: " ",
    field3: " ",
    field4: " ",
    field5: " ",
  });

  // Function to handle input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  useEffect(() => {
    // Count filled fields
    const filledFields = Object.values(formData).filter(value => value !== '' && value !== null && value !== undefined).length;
    // Update progress based on filled fields
    updateProgress(filledFields, totalFields);

    console.log(filledFields)
  }, [formData, updateProgress]);



  return (
    <div >
      <form className="forms">
        <input
          type="text"
          name="field1"
          value={formData.field1}
          onChange={handleChange}
          placeholder="Field 1"
        />
        <input
          type="text"
          name="field2"
          value={formData.field2}
          onChange={handleChange}
          placeholder="Field 2"
        />
        <input
          type="text"
          name="field3"
          value={formData.field3}
          onChange={handleChange}
          placeholder="Field 3"
        />
        <input
          type="text"
          name="field4"
          value={formData.field4}
          onChange={handleChange}
          placeholder="Field 4"
        />
        <input
          type="text"
          name="field5"
          value={formData.field5}
          onChange={handleChange}
          placeholder="Field 5"
        />
      </form>
    </div>
  );
}
