import React, { useState } from 'react'
import './step7.scss'

export default function Step1({ prevStep, nextStep }) {

  const [selectedOption, setSelectedOption] = useState(""); // Stan przechowujący wybór

  // Funkcja obsługująca zmianę zaznaczonej opcji
  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };



    return (
    <div className="step7">
      <h2 className="merriweather-light">
        Informacje pozostałe
      </h2>
      <div className="dates lato-light">
        <label>
          <input
            type="radio"
            name="account"
            value="Tak"
            onChange={handleOptionChange}
          />{" "}
          Tak
        </label>
        <label>
          <input
            type="radio"
            name="account"
            value="Nie"
            onChange={handleOptionChange}
          />{" "}
