import React, { useState } from "react";
import "./home2.scss";

// Karty z obrazami
const cards = [
  { id: 1, imageUrl: "/images/Tło usunięte (scene1).png" },
  { id: 2, imageUrl: "/images/Tło usunięte (scene2).png" },
  { id: 3, imageUrl: "/images/Tło usunięte (scene3).png" },
  { id: 4, imageUrl: "/images/Tło usunięte (scene4).png" },
];

export default function Home2() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [zIndexDelay, setZIndexDelay] = useState(false);

  const handleSwipe = (direction) => {
    setZIndexDelay(false);

    if (direction === "left" && currentIndex < cards.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else if (direction === "right" && currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }

    // Opóźniamy zmianę z-index po 1 sekundzie
    setTimeout(() => {
        setZIndexDelay(true);
      }, 1000); 
  };

  console.log(currentIndex)


  return (
    <div className="home2-container">
        <div className="controls">
          <button onClick={() => handleSwipe("left")}>Left</button>
          <button onClick={() => handleSwipe("right")}>Right</button>
        </div>
      <div className="card-swipe-container">
      {cards.map((card, index) => (
          <div
            key={card.id}
            className={`card ${index === currentIndex ? "active" : index < currentIndex ? "left" : "card-stack"}`}
            style={{ zIndex: zIndexDelay && index === currentIndex ? 10 : cards.length - index - 1 }}
          >
            <img src={card.imageUrl} alt={`Card ${card.id}`} className="card-img" />
          </div>
        ))}

      </div>
    </div>
  );
}
