import React, { useState } from "react";
import "./step2.scss";
// import { faArrowLeft } from "@fortawesome/free-solid-svg-icons";

export default function Step2({ nextStep, prevStep }) {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
  });

  // Funkcja obsługująca zmianę w inputach
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Sprawdza, czy wszystkie pola są wypełnione
  const isFormValid = Object.values(formData).every((value) => value.trim() !== "");

  return (
    <div className="step2 ">
      <h2 className="merriweather-light">Poznajmy się zatem</h2>
      <div className="inputs">
      <input
          type="text"
          name="firstName"
          placeholder="Imię"
          value={formData.firstName}
          onChange={handleChange}
        />
        <input
          type="text"
          name="lastName"
          placeholder="Nazwisko"
          value={formData.lastName}
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        <input
          type="tel"
          name="phone"
          placeholder="Telefon"
          value={formData.phone}
          onChange={handleChange}
        />
      </div>

      <button className="back" onClick={prevStep}>&#x2190;</button>
      {/* Przycisk Dalej będzie nieaktywny, jeśli formularz nie jest wypełniony */}
      <button onClick={nextStep} disabled={!isFormValid}>
        Dalej
      </button>
    </div>
  );
}
