import React, { useState, useEffect } from "react";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";
import "./step3.scss";

export default function Step3({ nextStep, prevStep }) {
  const [formData, setFormData] = useState({
    miasto: "",
    ulica: "",
  });

  const [mapCenter, setMapCenter] = useState(null); // Przechowywanie pozycji na mapie

  // Funkcja obsługująca zmianę w inputach
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Funkcja wywoływana po opuszczeniu pola "Miasto"
  const handleBlur = () => {
    if (formData.miasto.trim() !== "") {
      // Tu można zintegrować API do pobierania współrzędnych miasta
      // Na potrzeby przykładu podajemy losową lokalizację
      const geocodeCity = async () => {
        // Tymczasowa lokalizacja dla przykładu
        const mockLocation = {
          lat: 52.2297, // Szerokość geograficzna Warszawy
          lng: 21.0122, // Długość geograficzna Warszawy
        };
        setMapCenter(mockLocation); // Ustawienie pozycji mapy
      };

      geocodeCity();
    }
  };

  // Sprawdza, czy wszystkie pola są wypełnione
  const isFormValid = Object.values(formData).every(
    (value) => value.trim() !== ""
  );

  return (
    <div className="step3">
      <h2 className="merriweather-light">Poznajmy się zatem</h2>
      <div className="inputs">
        <div className="input-wrapper">
          <input
            className="lato-light miasto-input"
            type="text"
            name="miasto"
            value={formData.miasto}
            onChange={handleChange}
            onBlur={handleBlur} // Wywołanie mapy po opuszczeniu pola
            required
          />
          <label className={`lato-light ${formData.miasto ? "active" : ""}`}>
            Miasto
          </label>
        </div>
        <div className="input-wrapper">
          <input
            className="lato-light ulica-input"
            type="text"
            name="ulica"
            value={formData.ulica}
            onChange={handleChange}
            required
          />
          <label className={`lato-light ${formData.ulica ? "active" : ""}`}>
            Ulica
          </label>
        </div>
      </div>

      {/* Mapa pojawia się po opuszczeniu pola Miasto */}
      {mapCenter && (
        <div className="map-container">
          <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
            <GoogleMap
              mapContainerStyle={{
                width: "100%",
                height: "400px",
              }}
              center={mapCenter}
              zoom={15}
            >
              <Marker position={mapCenter} />
            </GoogleMap>
          </LoadScript>
        </div>
      )}

      <button className="back" onClick={prevStep}>
        &#x2190;
      </button>
      <button onClick={nextStep} disabled={!isFormValid}>
        Dalej
      </button>
    </div>
  );
}
