import React, { useEffect, useState } from "react";
import './step5.scss';

export default function Step5({ nextStep, prevStep, skippedQuestions, setSkippedQuestions, data, setData, resetCurrentStep, addSkippedQuestion }) {
  
  const [formData, setFormData] = useState(() => {
    const savedData = localStorage.getItem('step5FormData');
    return savedData ? JSON.parse(savedData) : {
      ogrzewanie: '',
      wodaciepla: '',
      grzejniki: '',
      wentylacja: '',
      szyby: '',
    };
  });

  const [powOptions] = useState([
    'Brak informacji',
    'sieć miejska',
    'grzejniki elektryczne',
    'kocioł na biomase',
    'kocioł na ekogroszek',
    'kocioł węglowy',
    'kocioł olejowy',
    'kocioł gazowy w mieszkaniu',
    'kocioł gazowy w kotłowni',
    'kocioł gazowy w kotłowni zewn.',
    'energia geotermalna',
    'piec kaflowy',
  ]);

  const [wodaOptions] = useState([
    'Brak informacji',
    'sieć miejska',
    'elektryczny podgrzewacz akumulacyjny',
    'elektryczny podgrzewacz przepływowy',
    'kocioł na biomase',
    'kocioł na ekogroszek',
    'kocioł węglowy',
    'kocioł olejowy',
    'kocioł gazowy w mieszkaniu',
    'kocioł gazowy w kotłowni',
    'kocioł gazowy w kotłowni zewn.',
    'energia geotermalna',
    'piec kaflowy',
  ]);

  const [grzejnikOptions] = useState([
    'Brak informacji',
    'płytowe',
    'żeliwne',
    'członowe',
    'podłogowe',
    'ogrzewanie piecowe lub kominkowe',
  ]);

  const [wentylOptions] = useState([
    'Brak informacji',
    'grawitacyjna',
    'mechaniczna wywiewna',
    'mechaniczna wywiewno-nawiewna',
  ]);

  const [isSkipEnabled, setIsSkipEnabled] = useState(true);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    
    // Aktywowanie/dezaktywowanie przycisku "Pomiń"
    if (value === 'Brak informacji') {
      setIsSkipEnabled(false);
    } else {
      setIsSkipEnabled(true);
    }
  };

  const skipQuestion = (question, options) => {
    addSkippedQuestion(5, question, options);
  };

  const isFormValid = () => {
    const skipped = skippedQuestions || [];
    const allFieldsFilled = Object.values(formData).every((value) => value.trim() !== '');
    const allQuestionsSkipped = skipped.some((skipped) => skipped.step === 5);
    return allFieldsFilled || allQuestionsSkipped;
  };

  useEffect(() => {
    localStorage.setItem('step5FormData', JSON.stringify(formData));
  }, [formData]);

  return (
    <div className="step5">
      <h2 className="merriweather-light">Szczegółowe dane nieruchomości</h2>

      <div className="inputs">
        <div className="input-wrapper">
          <label className="lato-light">Rodzaj ogrzewania</label>
          <select
            className="lato-light"
            name="ogrzewanie"
            value={formData.ogrzewanie}
            onChange={handleChange}
            required
          >
            {powOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button onClick={() => skipQuestion('Rodzaj ogrzewania', powOptions)} disabled={!isSkipEnabled}>
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <label className="lato-light">Źródło ciepłej wody</label>
          <select
            className="lato-light"
            name="wodaciepla"
            value={formData.wodaciepla}
            onChange={handleChange}
            required
          >
            {wodaOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button onClick={() => skipQuestion('Źródło ciepłej wody', wodaOptions)} disabled={!isSkipEnabled}>
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <label className="lato-light">Rodzaj zainstalowanych grzejników</label>
          <select
            className="lato-light"
            name="grzejniki"
            value={formData.grzejniki}
            onChange={handleChange}
            required
          >
            {grzejnikOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button onClick={() => skipQuestion('Rodzaj zainstalowanych grzejników', grzejnikOptions)} disabled={!isSkipEnabled}>
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <label className="lato-light">Rodzaj wentylacji</label>
          <select
            className="lato-light"
            name="wentylacja"
            value={formData.wentylacja}
            onChange={handleChange}
            required
          >
            {wentylOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button onClick={() => skipQuestion('Rodzaj wentylacji', wentylOptions)} disabled={!isSkipEnabled}>
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <label className="lato-light">Liczba szyb w oknach [szt]</label>
          <input
            className="lato-light"
            type="number"
            name="szyby"
            value={formData.szyby}
            onChange={handleChange}
            required
          />
          <button onClick={() => skipQuestion('Liczba szyb w oknach')} disabled={!isSkipEnabled}>
            Pomiń
          </button>
        </div>
      </div>

      <button className="back" onClick={prevStep}>
        &#x2190;
      </button>
      <button onClick={nextStep} disabled={!isFormValid()}>
        Dalej
      </button>
    </div>
  );
}
