import React, { useState, useRef, useEffect } from "react";
import "./fileUploader.scss"; // Stylizacja komponentu

const FileUploader = ({ label, onFileSelect }) => {
  const [file, setFile] = useState(null);
  const [fileUrl, setFileUrl] = useState(null);
  const fileInputRef = useRef(null);

  // Funkcja do obsługi zmiany pliku
  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];

    setFile(selectedFile);
    onFileSelect(selectedFile);

    if (selectedFile) {
      const objectUrl = URL.createObjectURL(selectedFile);
      setFileUrl(objectUrl);
    }
  };

  // Zwalniamy pamięć po usunięciu komponentu lub zmiany pliku
  useEffect(() => {
    return () => {
      if (fileUrl) {
        URL.revokeObjectURL(fileUrl);
      }
    };
  }, [fileUrl]);

  // Funkcja do obsługi kliknięcia przycisku (otwiera okno wyboru pliku)
  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="file-uploader">
      <button className="upload-button" onClick={handleButtonClick}>
        {label}
      </button>
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: "none" }}
        onChange={handleFileChange}
        accept="image/*"
      />
      {file ? (
        <div className="file-preview">
          <img src={fileUrl} alt="preview" className="file-thumbnail" />
        </div>
      ) : (
        <p className="no-file">Nie wybrano pliku</p>
      )}
    </div>
  );
};

export default FileUploader;
