import React, { useState } from 'react';
import './step8.scss';

export default function Step8({ nextStep, prevStep }) {
  const [files, setFiles] = useState([]);

  // Funkcja obsługująca zmianę plików
  const handleFileChange = (e) => {
    const newFiles = Array.from(e.target.files);
    setFiles((prevFiles) => [...prevFiles, ...newFiles]);
  };

  // Funkcja do usuwania pliku
  const removeFile = (fileToRemove) => {
    setFiles((prevFiles) => prevFiles.filter((file) => file !== fileToRemove));
  };

  return (
    <div className="step8">
      <h2>Załaduj zdjęcia</h2>

    <div className="fileUploaders">
      {/* Ukrywamy domyślny input typu file */}
      <label className="custom-file-upload">
        <input type="file" multiple onChange={handleFileChange} />
      </label>

      {/* Wyświetlanie listy załadowanych plików */}
      <div className="file-preview">
        {files.length === 0 && <p>Brak załadowanych plików</p>}
        {files.map((file, index) => (
          <div key={index} className="file-item">
            <p>{file.name}</p>
            <button onClick={() => removeFile(file)}>Usuń</button>
          </div>
        ))}
      </div>

      {/* Ukrywamy domyślny input typu file */}
      <label className="custom-file-upload">
        <input type="file" multiple onChange={handleFileChange} />
      </label>

      {/* Wyświetlanie listy załadowanych plików */}
      <div className="file-preview">
        {files.length === 0 && <p>Brak załadowanych plików</p>}
        {files.map((file, index) => (
          <div key={index} className="file-item">
            <p>{file.name}</p>
            <button onClick={() => removeFile(file)}>Usuń</button>
          </div>
        ))}
      </div>

      {/* Ukrywamy domyślny input typu file */}
      <label className="custom-file-upload">
        <input type="file" multiple onChange={handleFileChange} />
      </label>

      {/* Wyświetlanie listy załadowanych plików */}
      <div className="file-preview">
        {files.length === 0 && <p>Brak załadowanych plików</p>}
        {files.map((file, index) => (
          <div key={index} className="file-item">
            <p>{file.name}</p>
            <button onClick={() => removeFile(file)}>Usuń</button>
          </div>
        ))}
      </div>

    </div>



      <button onClick={prevStep}>Wstecz</button>
      <button onClick={nextStep}>Zakończ</button>
    </div>
  );
}
