import React, { useEffect, useState } from 'react';
import './step5.scss';

export default function Step5({ nextStep, prevStep, skippedQuestions, setSkippedQuestions, data, setData, resetCurrentStep, addSkippedQuestion }) {
  
  const [formData, setFormData] = useState(() => {
    const savedData = localStorage.getItem('step5FormData');
    return savedData
      ? JSON.parse(savedData)
      : {
          ogrzewanie: '',
          wodaciepla: '',
          grzejniki: '',
          wentylacja: '',
          szyby: '',
        };
  });

  const [powOptions] = useState([
    'sieć miejska',
    'grzejniki elektryczne',
    'kocioł na biomase',
    'kocioł na ekogroszek',
    'kocioł węglowy',
    'kocioł olejowy',
    'kocioł gazowy w mieszkaniu',
    'kocioł gazowy w kotłowni',
    'kocioł gazowy w kotłowni zewn.',
    'energia geotermalna',
    'piec kaflowy',
  ]);

  const [wodaOptions] = useState([
    'sieć miejska',
    'elektryczny podgrzewacz akumulacyjny',
    'elektryczny podgrzewacz przepływowy',
    'kocioł na biomase',
    'kocioł na ekogroszek',
    'kocioł węglowy',
    'kocioł olejowy',
    'kocioł gazowy w mieszkaniu',
    'kocioł gazowy w kotłowni',
    'kocioł gazowy w kotłowni zewn.',
    'energia geotermalna',
    'piec kaflowy',
  ]);

  const [grzejnikOptions] = useState([
    'płytowe',
    'żeliwne',
    'członowe',
    'podłogowe',
    'ogrzewanie piecowe lub kominkowe',
  ]);

  const [wentylOptions] = useState([
    'grawitacyjna',
    'mechaniczna wywiewna',
    'mechaniczna wywiewno-nawiewna',
  ]);

  // Stany aktywności przycisków "Pomiń"
  const [skipButtonsDisabled, setSkipButtonsDisabled] = useState({
    ogrzewanie: false,
    wodaciepla: false,
    grzejniki: false,
    wentylacja: false,
    szyby: false,
  });

  // Funkcja obsługująca zmianę w inputach
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Zablokuj przycisk "Pomiń", jeśli wybrano opcję z listy
    setSkipButtonsDisabled((prev) => ({
      ...prev,
      [name]: true,
    }));
  };

  // Funkcja obsługująca pomijanie pytań
  const skipQuestion = (question, options) => {
    addSkippedQuestion(5, question, options);

    // Zablokuj odpowiedni przycisk "Pomiń"
    setSkipButtonsDisabled((prev) => ({
      ...prev,
      [question.toLowerCase()]: true,
    }));
  };

  // Sprawdza, czy wszystkie pola są wypełnione lub czy wszystkie pytania zostały pominięte
  const isFormValid = () => {
    const allFieldsFilled = Object.values(formData).every((value) => value.trim() !== '');
    const allQuestionsSkipped = skippedQuestions.some((skipped) => skipped.step === 5);
    return allFieldsFilled || allQuestionsSkipped;
  };

  useEffect(() => {
    // Zapisywanie stanu do localStorage po każdej zmianie
    localStorage.setItem('step5FormData', JSON.stringify(formData));
  }, [formData]);

  return (
    <div className="step5">
      <h2 className="merriweather-light">Szczegółowe dane nieruchomości</h2>

      <div className="inputs">
        <div className="input-wrapper">
          <select
            className="lato-light"
            name="ogrzewanie"
            value={formData.ogrzewanie}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Wybierz rodzaj ogrzewania
            </option>
            {powOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button
            onClick={() => skipQuestion('ogrzewanie', powOptions)}
            disabled={skipButtonsDisabled.ogrzewanie}
          >
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <select
            className="lato-light"
            name="wodaciepla"
            value={formData.wodaciepla}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Źródło ciepłej wody
            </option>
            {wodaOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button
            onClick={() => skipQuestion('wodaciepla', wodaOptions)}
            disabled={skipButtonsDisabled.wodaciepla}
          >
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <select
            className="lato-light"
            name="grzejniki"
            value={formData.grzejniki}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Rodzaj zainstalowanych grzejników
            </option>
            {grzejnikOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button
            onClick={() => skipQuestion('grzejniki', grzejnikOptions)}
            disabled={skipButtonsDisabled.grzejniki}
          >
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <select
            className="lato-light"
            name="wentylacja"
            value={formData.wentylacja}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Rodzaj wentylacji
            </option>
            {wentylOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
          <button
            onClick={() => skipQuestion('wentylacja', wentylOptions)}
            disabled={skipButtonsDisabled.wentylacja}
          >
            Pomiń
          </button>
        </div>

        <div className="input-wrapper">
          <input
            className="lato-light"
            type="number"
            name="szyby"
            value={formData.szyby}
            onChange={handleChange}
            required
          />
          <label className={`lato-light ${formData.szyby ? 'active' : ''}`}>
            Liczba szyb w oknach [szt]
          </label>
          <button
            onClick={() => skipQuestion('szyby')}
            disabled={skipButtonsDisabled.szyby}
          >
            Pomiń
          </button>
        </div>
      </div>

      <button className="back" onClick={prevStep}>
        &#x2190;
      </button>
      <button onClick={nextStep} disabled={!isFormValid()}>
        Dalej
      </button>
    </div>
  );
}
