import React, { useState } from "react";
import { motion } from "framer-motion";
import "./home2.scss";

const cards = [
  { id: 1, imageUrl: "/images/Tło usunięte (scene1).png" },
  { id: 2, imageUrl: "/images/Tło usunięte (scene2).png" },
  { id: 3, imageUrl: "/images/Tło usunięte (scene3).png" },
  { id: 4, imageUrl: "/images/Tło usunięte (scene4).png" },
];

export default function Home2() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleSwipe = (direction) => {
    setCurrentIndex((prevIndex) =>
      direction === "left"
        ? Math.min(prevIndex + 1, cards.length - 1)
        : Math.max(prevIndex - 1, 0)
    );
  };

  const getCardStyle = (index) => {
    const distance = Math.abs(index - currentIndex);
    const offset = distance * 10;
    const scale = 1 - distance * 0.05;
    return {
      transform: `translateX(${index < currentIndex ? -35 - offset : 35 + offset}px) scale(${scale})`,
    };
  };

  return (
    <div className="home2-container">
      <div className="controls">
        <button onClick={() => handleSwipe("left")}>Left</button>
        <button onClick={() => handleSwipe("right")}>Right</button>
      </div>
      <div className="card-swipe-container">
        {cards.map((card, index) => (
          <motion.div
            key={card.id}
            className="card"
            initial={false}
            animate={{
              zIndex: currentIndex === index ? 10 : index < currentIndex ? 9 : 8,
              x: currentIndex === index ? 0 : index < currentIndex ? -35 : 35,
              scale: currentIndex === index ? 1 : 1 - Math.abs(index - currentIndex) * 0.05,
            }}
            transition={{ type: "spring", stiffness: 200, damping: 20 }}
            style={getCardStyle(index)}
          >
            <img src={card.imageUrl} alt={`Card ${card.id}`} className="card-img" />
          </motion.div>
        ))}
      </div>
    </div>
  );
}
