import React, { useState } from "react";
import "./step6.scss";

export default function Step6({ nextStep, prevStep }) {
  const [formData, setFormData] = useState({
    ogrzewanie: "",
    wodaciepla: "",
    grzejniki: "",
    wentylacja: "",
    szyby: "",
  });

  const [powOptions, setPowOptions] = useState([
    "sieć miejska",
    "grzejniki elektryczne",
    "kocioł na biomase",
    "kocioł na ekogroszek",
    "kocioł węglowy",
    "kocioł olejowy",
    "kocioł gazowy w mieszkaniu",
    "kocioł gazowy w kotłowni",
    "kocioł gazowy w kotłowni zewn.",
    "energia geotermalna",
    "piec kaflowy",
  ]);
  const [wodaOptions, setWodaOptions] = useState([
    "sieć miejska",
    "elektryczny podgrzewacz akumulacyjny",
    "elektryczny podgrzewacz przepływowy",
    "kocioł na biomase",
    "kocioł na ekogroszek",
    "kocioł węglowy",
    "kocioł olejowy",
    "kocioł gazowy w mieszkaniu",
    "kocioł gazowy w kotłowni",
    "kocioł gazowy w kotłowni zewn.",
    "energia geotermalna",
    "piec kaflowy",
  ]);
  const [grzejnikOptions, setGrzejnikOptions] = useState([
    "płytowe",
    "zeliwne",
    "członowe",
    "podłogowe",
    "ogrzewanie piecowe lub kominkowe",

  ]);
  const [wentylOptions, setWentylOptions] = useState([
    "grawitacyjna",
    "mechaniczna wywiewna",
    "mechaniczna wywiewno-nawiewna",
  ]);



  // Funkcja obsługująca zmianę w inputach
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  console.log(formData);

  // Sprawdza, czy wszystkie pola są wypełnione
  const isFormValid = Object.values(formData).every(
    (value) => value.trim() !== ""
  );

  return (
    <div className="step6">
      <h2 className="merriweather-light">Szczegółowe dane nieruchomości</h2>

      <div className="inputs">
        <div className="input-wrapper">
          <select
            className="lato-light"
            name="ogrzewanie"
            value={formData.ogrzewanie}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Wybierz rodzaj ogrzewania
            </option>
            {powOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
        </div>
        <div className="input-wrapper">
          <select
            className="lato-light"
            name="wodaciepla"
            value={formData.wodaciepla}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Źródło ciepłej wody
            </option>
            {wodaOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
        </div>
        <div className="input-wrapper">
          <select
            className="lato-light"
            name="grzejniki"
            value={formData.grzejniki}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Rodzaj zainstalowanych grzejników
            </option>
            {grzejnikOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
        </div>

        <div className="input-wrapper">
          <select
            className="lato-light"
            name="wentylacja"
            value={formData.wentylacja}
            onChange={handleChange}
            required
          >
            <option value="" disabled>
              Rodzaj wentylacji
            </option>
            {wentylOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
        </div>

        <div className="input-wrapper">
          <input
            className="lato-light"
            type="number"
            name="szyby"
            value={formData.szyby}
            onChange={handleChange}
            required
          />
          <label className={`lato-light ${formData.szyby ? "active" : ""}`}>
            Liczba szyb w oknach [szt]
          </label>
        </div>
      </div>

      <button className="back" onClick={prevStep}>
        &#x2190;
      </button>
      <button onClick={nextStep} disabled={!isFormValid}>
        Dalej
      </button>
    </div>
  );
}
