import React, { useEffect, useState } from "react";
import "./formComponent.scss";

export default function FormComponent({ updateProgress }) {
  const totalFields = 10;
  const [formData, setFormData] = useState({
    field1: "",
    field2: "",
    field3: "",
    field4: "",
    field5: "",
    field6: "",
    field7: "",
    field8: "",
    field9: "",
    field10: "",
  });

  // Function to handle input changes in real-time
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Function to handle input blur (field loses focus)
  const handleBlur = (e) => {
    const updatedFormData = {
      ...formData,
      [e.target.name]: e.target.value,
    };
    setFormData(updatedFormData);
  };

  useEffect(() => {
    // Count filled fields
    const filledFields = Object.values(formData).filter(
      (value) => value !== "" && value !== null && value !== undefined
    ).length;
    // Update progress based on filled fields
    updateProgress(filledFields, totalFields);
  }, [formData, updateProgress]);

  return (
    <div>
      <form className="forms">
        <input
          type="text"
          name="field1"
          value={formData.field1}
          onChange={handleChange} // Aktualizacja w czasie rzeczywistym
          onBlur={handleBlur} // Aktualizacja po opuszczeniu pola
          placeholder="Field 1"
        />
        <input
          type="text"
          name="field2"
          value={formData.field2}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 2"
        />
        <input
          type="text"
          name="field3"
          value={formData.field3}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 3"
        />
        <input
          type="text"
          name="field4"
          value={formData.field4}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 4"
        />
        <input
          type="text"
          name="field5"
          value={formData.field5}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 5"
        />
        <input
          type="text"
          name="field6"
          value={formData.field6}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 6"
        />
        <input
          type="text"
          name="field7"
          value={formData.field7}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 7"
        />
        <input
          type="text"
          name="field8"
          value={formData.field8}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 8"
        />
        <input
          type="text"
          name="field9"
          value={formData.field9}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 9"
        />
        <input
          type="text"
          name="field10"
          value={formData.field10}
          onChange={handleChange}
          onBlur={handleBlur}
          placeholder="Field 10"
        />
      </form>
    </div>
  );
}
