import React, { useState } from "react";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";
import "./step3.scss";

export default function Step3({ nextStep, prevStep }) {
  const [formData, setFormData] = useState({
    miasto: "",
    ulica: "",
  });

  // Ustawienie stałych współrzędnych
  const [mapCenter, setMapCenter] = useState({ lat: 50.3020313, lng: 18.638388 }); // Przykładowe współrzędne

  // Funkcja obsługująca zmianę w inputach
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Sprawdza, czy wszystkie pola są wypełnione
  const isFormValid = Object.values(formData).every(
    (value) => value.trim() !== ""
  );

  return (
    <div className="step3">
      <h2 className="merriweather-light">Adres nieruchomości</h2>

      {/* Mapa Google wyświetlana po wypełnieniu formularza */}
      {mapCenter && (
        <div className="map-container">
          <LoadScript googleMapsApiKey="AIzaSyCmAwRE3ftUWNr5245Wb79HdqmNfya6QSo">
            <GoogleMap
              mapContainerStyle={{
                width: "100%",
                height: "200px",
              }}
              center={mapCenter}
              zoom={15}
            >
              <Marker position={mapCenter} />
            </GoogleMap>
          </LoadScript>
        </div>
      )}

      <div className="inputs">
        <div className="input-wrapper">
          <input
            className="lato-light miasto-input"
            type="text"
            name="miasto"
            value={formData.miasto}
            onChange={handleChange}
            required
          />
          <label className={`lato-light ${formData.miasto ? "active" : ""}`}>
            Miasto
          </label>
        </div>
        <div className="input-wrapper">
          <input
            className="lato-light ulica-input"
            type="text"
            name="ulica"
            value={formData.ulica}
            onChange={handleChange}
            required
          />
          <label className={`lato-light ${formData.ulica ? "active" : ""}`}>
            Ulica
          </label>
        </div>
      </div>

      <button className="back" onClick={prevStep}>
        &#x2190;
      </button>
      <button onClick={nextStep} disabled={!isFormValid}>
        Dalej
      </button>
    </div>
  );
}
