import React, { useState, useEffect } from "react";
import "./chatPage.scss";

export default function ChatPage() {
  const [showChat, setShowChat] = useState(false);

  const handleNextClick = () => {
    setShowChat(true);
  };

  return (
    <div className="chatpage">
      <div className="chatback">
        <img src="/images/design7.png" alt="" />
        <hr className="full-width-line" />
      </div>

      {/* HeadPhoto, które zamieni się w awatar bota */}
      <div className={`headPhoto ${showChat ? "headPhoto-chat" : ""}`}>
        <img src="/images/profil.jpg" alt="Profile" />
      </div>

      {/* Conditional rendering of infoContainer and chatContainer */}
      <div className={`infoContainer ${showChat ? "infoContainer-hide" : ""}`}>
        <span>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Explicabo deserunt eveniet tempora debitis in ullam nihil quam optio alias ut.
        </span>
        <span>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Laborum saepe esse eius placeat! Omnis, explicabo! Repellendus, aperiam? Incidunt voluptatum, ut, deserunt a commodi aliquam, sapiente id veniam ex dolor totam.
        </span>
        <button onClick={handleNextClick}>Dalej</button>
      </div>

      {/* Chat container */}
      {showChat && (
        <div className="chatContainer chat-visible">
          <div className="chat-bot-message">
            {/* Tutaj używamy tego samego zdjęcia jako avatar bota */}
            <div className="bot-avatar">
              <img src="/images/profil.jpg" alt="Bot Avatar" />
            </div>
            <p>Welcome to the chat! I will guide you through the questions.</p>
          </div>
          <div className="chat-bot-message">
            <div className="bot-avatar">
              <img src="/images/profil.jpg" alt="Bot Avatar" />
            </div>
            <p>Here is another message from the bot.</p>
          </div>
          <div className="chat-user-message">
            <p>User's response will appear here.</p>
          </div>
        </div>
      )}
    </div>
  );
}
