import React, { useState } from "react";
import "./step7.scss";

export default function Step1({ prevStep, nextStep }) {
  const [selectedOption, setSelectedOption] = useState(""); // Stan przechowujący wybór

  // Funkcja obsługująca zmianę zaznaczonej opcji
  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  return (
    <div className="step7">
      <h2 className="merriweather-light">Informacje pozostałe</h2>
      <div className="dates lato-light">
        <label>
          <input
            type="radio"
            name="account"
            value="Tak"
            onChange={handleOptionChange}
          />{" "}
          Zaznacz, jeśli budynek posiada klimatyzację
        </label>
        <label>
          <input
            type="radio"
            name="account"
            value="Nie"
            onChange={handleOptionChange}
          />{" "}
          Zaznacz, jeśli budynek posiada fotowoltaikę
        </label>
        <label>
          <input
            type="radio"
            name="account"
            value="Tak"
            onChange={handleOptionChange}
          />{" "}
          Zaznacz, jeśli jest balkon/taras
        </label>
        <label>
          <input
            type="radio"
            name="account"
            value="Tak"
            onChange={handleOptionChange}
          />{" "}
          Zaznacz, jeśli mieszkanie jest na parterze
        </label>
        <label>
          <input
            type="radio"
            name="account"
            value="Tak"
            onChange={handleOptionChange}
          />{" "}
          Zaznacz, jeśli mieszkanie jest na ostatnim piętrze
        </label>
      </div>

      {/* Przycisk "Dalej" jest aktywny tylko, gdy wybrano jedną z opcji */}
      <button className="back" onClick={prevStep}>
        &#x2190;
      </button>
      <button className="lato-light" onClick={nextStep}>
        Dalej
      </button>
    </div>
  );
}
