import React, { useState } from "react";
import "./formPage.scss";
import Step1 from "../../components/forms/step1/Step1";
import Step2 from "../../components/forms/step2/Step2";
import Step3 from "../../components/forms/step3/Step3";
import Step4 from "../../components/forms/step4/Step4";
import Step5 from "../../components/forms/step5/Step5";
import Step6 from "../../components/forms/step6/Step6";
import Step7 from "../../components/forms/step7/Step7";
import Step8 from "../../components/forms/step8/Step8";

export default function FormPage() {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    step1Data: {},
    step2Data: {},
    step3Data: {},
    step4Data: {},
    step5Data: {},
    step6Data: {},
    step7Data: {},
    step8Data: {},
    // Inne dane kroków
  });

  // Funkcja do przełączania między sekcjami
  const nextStep = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const prevStep = () => {
    if (step > 1) {
      setCurrentStep((prevStep) => prevStep - 1);
    }
  };

  // Funkcja do resetowania stanu aktualnego kroku
  const resetCurrentStep = () => {
    setFormData((prevData) => ({
      ...prevData,
      [`step${currentStep}Data`]: {},
    }));
  };

  // Funkcja do renderowania odpowiedniej sekcji formularza
  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <Step1
            data={formData.step1Data}
            setData={(data) => setFormData({ ...formData, step1Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      case 2:
        return (
          <Step2
            data={formData.step2Data}
            setData={(data) => setFormData({ ...formData, step2Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      case 3:
        return (
          <Step3
            data={formData.step3Data}
            setData={(data) => setFormData({ ...formData, step3Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      case 4:
        return (
          <Step4
            data={formData.step4Data}
            setData={(data) => setFormData({ ...formData, step4Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      case 5:
        return (
          <Step5
            data={formData.step5Data}
            setData={(data) => setFormData({ ...formData, step5Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      case 6:
        return (
          <Step6
            data={formData.step6Data}
            setData={(data) => setFormData({ ...formData, step6Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      case 7:
        return (
          <Step7
            data={formData.step7Data}
            setData={(data) => setFormData({ ...formData, step7Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      case 8:
        return (
          <Step8
            data={formData.step8Data}
            setData={(data) => setFormData({ ...formData, step8Data: data })}
            nextStep={nextStep}
            prevStep={prevStep}
            resetCurrentStep={resetCurrentStep}
          />
        );
      default:
        return <Step1 nextStep={nextStep} />;
    }
  };

  return (
    <div className="form-page">
      <div className="headPhoto">
        <img src="/images/profil.jpg" alt="" />
      </div>
      <hr class="full-width-line" />

      {/* Sekcja po lewej stronie */}
      <div className="left-section">
        {/* Tutaj można wstawić grafikę */}
        <img
          src="/images/back05 2.png"
          alt="Reacting"
          className="reacting-image"
        />
      </div>

      {/* Sekcja formularza po prawej stronie */}
      <div className="right-section">
        {renderStep()} {/* Wyświetla odpowiednią sekcję formularza */}
      </div>
    </div>
  );
}
