import React, { useState } from "react";
import "./home5.scss";

const faqs = [
  {
    question: "What is a FAQ?",
    answer:
      "FAQ stands for Frequently Asked Questions, a list of common questions and their answers.",
  },
  {
    question: "Why use a FAQ section?",
    answer:
      "A FAQ section helps address common concerns or questions your audience may have.",
  },
  {
    question: "How do I create a FAQ?",
    answer:
      "Using React, you can toggle the visibility of answers with state management and simple event handling.",
  },
];

const QAItem = ({ faq, index, toggleFAQ, isOpen }) => {
  return (
    <div className="faq-item">
      <div className="faq-question" onClick={() => toggleFAQ(index)}>
        {faq.question}
        <span className="faq-toggle-icon">{isOpen ? "-" : "+"}</span>
      </div>
      {isOpen && <div className="faq-answer">{faq.answer}</div>}
    </div>
  );
};

export default function QAList() {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index); // Toggles open/close
  };
  return (
    <div className="faq-list">
      {faqs.map((faq, index) => (
        <QAItem
          key={index}
          faq={faq}
          index={index}
          toggleFAQ={toggleFAQ}
          isOpen={openIndex === index}
        />
      ))}
    </div>
  );
}
