import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import "./home2.scss";

const cards = [
  { id: 1, imageUrl: "/images/Tło usunięte (scene1).png" },
  { id: 2, imageUrl: "/images/Tło usunięte (scene2).png" },
  { id: 3, imageUrl: "/images/Tło usunięte (scene3).png" },
  { id: 4, imageUrl: "/images/Tło usunięte (scene4).png" },
];

export default function Home2() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleSwipe = (direction) => {
    setCurrentIndex((prevIndex) =>
      direction === "left"
        ? Math.min(prevIndex + 1, cards.length - 1)
        : Math.max(prevIndex - 1, 0)
    );
  };

  return (
    <div className="home2-container">
      <div className="controls">
        <button onClick={() => handleSwipe("left")}>Left</button>
        <button onClick={() => handleSwipe("right")}>Right</button>
      </div>
      <div className="card-swipe-container">
        {cards.map((card, index) => {
          const springProps = useSpring({
            transform: `translateX(${
              index < currentIndex ? -35 : index > currentIndex ? 35 : 0
            }px) scale(${index === currentIndex ? 1 : 1 - Math.abs(index - currentIndex) * 0.05})`,
            config: { tension: 280, friction: 60 },
          });

          return (
            <animated.div
              key={card.id}
              className="card"
              style={{ ...springProps, zIndex: index === currentIndex ? 10 : index < currentIndex ? 9 : 8 }}
            >
              <img src={card.imageUrl} alt={`Card ${card.id}`} className="card-img" />
            </animated.div>
          );
        })}
      </div>
    </div>
  );
}
